class SubTest {
	public static void main(String[] args) {
	    int a = 42;
	    int b = 58;
	    System.out.println(a - b);
	    System.out.println(50 - 50);
	    
	    int c = 2147483647;
	    int d = -2147483648;
	    System.out.println(d);
	    System.out.println(d - 1);
	    System.out.println(d - 1 - c);
	}
}