class Test {
	public static void main(String[] args) {
		int i = 17;	
		while(i > 11) {	
			i = i + 7;
		}
		i = i - 1;
		System.out.println(i);
		
		int j = -19;	
		while(j < 9) {
			j = j - 1;
		}
		j = j + 1;
		System.out.println(j);
		
		int k = 4;
		while(k <= 9) {
			k = k - 12;
		}
		k = k + 1;
		System.out.println(k);
		
		int a = 6;
		while(a > 4) {
			a = a + 11;
		}
		a = a - 1;
		System.out.println(a);
	}
}