class Test {
	public static void main(String[] args) {
		int i = 3;
		while (i < 17) {
			i = i + 2;
		}
		System.out.println(i);

		int j = 3;
		while (j <= 17) {
			j = j + 2;
		}
		System.out.println(j);

		int k = 17;
		while (k > 3) {
			k = k - 2;
		}
		System.out.println(k);

		int a = 17;
		while (a >= 3) {
			a = a - 2;
		}
		System.out.println(a);
	}
}